            name: "ricky",
            age: 19
        }
    ]
    const [person, setPersons] = useState(initialstate)
    const changePersons = () => 
    {
        setPersons([
            {name : 'keysa', age: 21},
            {name : 'adi', age: 19},
            {name : 'silvy', age: 22}
        ])
    }
    return 
    (
        <>
        {
            person.map(person => (
